mapper.update:
UPDATE user
SET is_block = true;
WHERE id = #{id}

UPDATE user
SET is_block = false;
WHERE id = #{id}

UPDATE user
SET is_quit = true;
WHERE id = #{id}

mapper.select:
select password
from user
where user_id = #{userId}

select id, user_id, password, nickname, is_block, is_quit
from user
where user_id = #{userId}
