mapper.select:
SELECT wish_history.*
FROM wish_history
WHERE id = #{id}

mapper.insert:
INSERT INTO wish_history (id, wish_id, history_datetime, amount, register_datetime)
VALUES (#{id}, #{wishId}, #{historyDatetime}, #{amount}, #{registerDatetime},
WHERE wish_id = #{wishId}

mapper.update:
UPDATE wishHistory
SET history_datetime = #{historyDatetime},
    amount           = #{amount}
WHERE id = #{id}
